def multiTwoBigger(s1, s2):
    """ 大整数相加，python好像不会溢出 """

    if len(s1) == 0 and len(s2) == 0:
        return "0"

    index1 = len(s1) - 1
    index2 = len(s2) - 1

    result = ""
    left = 0
    while index1>=0 and index2>=0:
        c1 = s1[index1]
        c2 = s2[index2]

        sum = int(c1) + int(c2) + left
        if sum>=10:
            left = 1
        else:
            left = 0
        c3 = sum % 10
        result = "%d%s" % (c3, result)
        index1 -= 1
        index2 -= 1

    def Common(s, index, result, left):
        while index>=0:
            c1 = s[index]
            sum = int(c1) + left
            if sum>=10:
                 left = 1
            else:
                left = 0
            c3 = sum % 10
            result = "%d%s" % (c3, result)
            index -= 1
        return result, left

    result, left = Common(s1, index1, result, left)
    result, left = Common(s2, index2, result, left)

    if left:
        result = "%d%s" % (1, result)
    return result

